import { Col } from "react-bootstrap";

export const ProjectCard = ({ title, description, imgUrl, prof }) => {
  return (
    <Col size={12} sm={6} md={4}>
      <a href={prof} target="_blank" rel="noopener noreferrer" className="proj-link">
        <div 
          className="proj-imgbx" 
          style={{
            height: "350px",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
            alignItems: "center",
            textAlign: "center",
            borderRadius: "10px",
            overflow: "hidden",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
            backgroundColor: "#fff",
            padding: "10px"
          }}
        >
          <div style={{
            width: "100%",
            height: "200px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            overflow: "hidden",
          }}>
            <img 
              src={imgUrl} 
              alt={title} 
              style={{
                maxWidth: "100%",
                maxHeight: "100%",
                objectFit: "contain"
              }} 
            />
          </div>
          <div className="proj-txtx" style={{ padding: "10px", width: "100%" }}>
            <h4 style={{ color: "black", margin: "5px 0" }}>{title}</h4>
            <span style={{ color: "white" }}>Click here to open ...</span>
          </div>
        </div>
      </a>
    </Col>
  );
};
