import { Col } from "react-bootstrap";

export const ProjectCard = ({ title, description, imgUrl, prof }) => {
  return (
    <Col xs={12} sm={6} md={4}>
      <a
        href={prof}
        target="_blank"
        rel="noopener noreferrer"
        className="proj-link"
        style={{ textDecoration: "none" }}
      >
        <div
          className="proj-imgbx"
          style={{
            height: "100%",
            display: "flex",
            flexDirection: "column",
            justifyContent: "space-between",
            alignItems: "center",
            textAlign: "center",
            borderRadius: "10px",
            overflow: "hidden",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
            backgroundColor: "#fff",
            padding: "15px",
            minHeight: "350px",
          }}
        >
          <img
            src={imgUrl}
            alt={title}
            style={{
              width: "100%",
              height: "auto",
              objectFit: "contain",
              maxHeight: "200px",
            }}
          />
          <div className="proj-txtx" style={{ padding: "10px", width: "100%" }}>
            <h4 style={{ color: "#333", margin: "5px 0" }}>{title}</h4>
            <span style={{ color: "#555" }}>Click here to open...</span>
          </div>
        </div>
      </a>
    </Col>
  );
};
