import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Github } from "lucide-react";

const projects = [
  { title: "Project One", github: "https://github.com/user/project1" },
  { title: "Project Two", github: "https://github.com/user/project2" },
  { title: "Project Three", github: "https://github.com/user/project3" },
  { title: "Project Four", github: "https://github.com/user/project4" },
  { title: "Project Five", github: "https://github.com/user/project5" },
  { title: "Project Six", github: "https://github.com/user/project6" },
];

export default function ProjectsSection() {
  return (
    <section className="py-10 bg-gray-900 text-white" id="projects">
      <div className="container mx-auto text-center">
        <h2 className="text-3xl font-bold mb-6">Projects</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6">
          {projects.map((project, index) => (
            <motion.div
              key={index}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              className="p-4"
            >
              <Card className="bg-gray-800 p-4 rounded-xl shadow-lg">
                <CardContent className="flex flex-col items-center">
                  <h3 className="text-lg font-semibold mb-2">{project.title}</h3>
                  <Button
                    asChild
                    className="mt-2 bg-gray-700 hover:bg-gray-600 text-white flex items-center gap-2"
                  >
                    <a href={project.github} target="_blank" rel="noopener noreferrer">
                      <Github className="w-5 h-5" /> GitHub
                    </a>
                  </Button>
                </CardContent>
              </Card>
            </motion.div>
          ))}
        </div>
      </div>
    </section>
  );
}
